local function GetEnemyJunglers()
	local _enemyJunglers
    if _enemyJunglers then return _enemyJunglers end
    _enemyJunglers = {}
	for i, hero in pairs(enemy) do
		if hero:GetSpellData(SUMMONER_1).name == "SummonerSmite" or hero:GetSpellData(SUMMONER_1).name == "S5_SummonerSmiteDuel" or hero:GetSpellData(SUMMONER_1).name == "S5_SummonerSmitePlayerGanker" then
			_enemyJunglers[#_enemyJunglers+1] = hero
		elseif hero:GetSpellData(SUMMONER_2).name == "SummonerSmite" or hero:GetSpellData(SUMMONER_2).name == "S5_SummonerSmiteDuel" or hero:GetSpellData(SUMMONER_2).name == "S5_SummonerSmitePlayerGanker" then
			_enemyJunglers[#_enemyJunglers+1] = hero
		end
	end
    return setmetatable(_enemyJunglers,{
        __newindex = function(self, key, value)
            error("Adding to EnemyJunglers is not granted. Use table.copy.")
        end,
    })
end

