			DrawTextA("F", 14, x - 23, y + 25, ARGB(255, 0, 255, 0), "right", "center")
		else
			DrawTextA("F:"..mfloor(hero:GetSpellData(SUMMONER_2).currentCd).."s", 14, x - 23, y + 25, ARGB(255, 255, 0, 0), "right", "center")
		end
